window.addEventListener("load", function () {
    document.getElementById("email").focus();
});

document.getElementById("login").addEventListener("click", function (event) {
    login();
});

document.getElementById("password").addEventListener("keypress", function (event) {
    if (event.key === "Enter") {
        login();
    }
});

document.getElementById("email").addEventListener("keypress", function (event) {
    if (event.key === "Enter") {
        login();
    }
});



function login() {
    email = document.getElementById("email").value
    password = document.getElementById("password").value

    if (validate_email(email) == false || validate_password(password) == false) {
        document.getElementById("check-false").style.display = "block";
        document.getElementById("check-true").style.display = "none";
        document.getElementById("password").value = "";
        return
    }

    auth.signInWithEmailAndPassword(email, password)
        .then(function () {
            var user = auth.currentUser
            
            var database_ref = database.ref()

            var user_data = {
                email: email,
                last_login: Date.now()
            }

            database_ref.child("users/" + user.uid).update(user_data)

            document.getElementById("check-true").innerHTML = "*Zalogowano&nbsp;uÅ¼ytkownika:&nbsp;" + email.substring(0, email.indexOf("@"));
            document.getElementById("check-true").style.display = "block";
            document.getElementById("check-false").style.display = "none";

            document.getElementById("logout").style.display = "block";
            document.getElementById("login").style.display = "none";

        })
}




function validate_email(email) {
    expression = /^[^@]+@\w+(\.\w+)+\w$/
    if (expression.test(email) == true) {
        return true
    } else {
        return false
    }
}

function validate_password(password) {
    if (password < 6) {
        return false
    } else {
        return true
    }
}






document.getElementById("logout").addEventListener("click", function (event) {
    logout();
});



function logout() {
    auth.signOut().then(function () {
        window.location.reload(true);

    }).catch((error) => {
        alert("error")
    });
}
